import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
    html {
        font-family: 'Sofia Pro',Sans-Serif;
        font-size: 62.5%;
        scroll-behavior: smooth;
        overflow-y: scroll;

        /* font sizes */
        --header1: 5.6rem;
        --header2: calc(2.5rem + 1vw);
        --header3: calc(2rem + 1vw);
        --header4: calc(1.8rem + 1vw);
        --text: 2rem;
        --links: 1.4rem;

        /* font colors */
        --nav-color:#000000;
        --nav-order-color:#FFFFFF;
        --discount-color:#FFFFFF;

        /* background colors */
        --primary-bg: #ffffff;
        --secondary-bg:#920303;
        --third-bg:#000000;
    }

    body {
        max-width:1920px;
        font-size: 1.6rem;
    }

    button{
        cursor:pointer;
    }

`

export default GlobalStyle;
